template.template_css:
@font-face {
  font-family: Fira Sans;
  font-weight: 400;
  src: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/firasans-regular.otf);
}
@font-face {
  font-family: Fira Sans;
  font-style: italic;
  font-weight: 400;
  src: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/firasans-italic.otf);
}
@font-face {
  font-family: Fira Sans;
  font-weight: 300;
  src: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/firasans-light.otf);
}
@font-face {
  font-family: Fira Sans;
  font-style: italic;
  font-weight: 300;
  src: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/firasans-lightitalic.otf);
}
@font-face {
  font-family: Fira Sans;
  font-weight: 700;
  src: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/firasans-bold.otf);
}

@page {
  size: landscape;
  @top-left {
    background: #ffcc00;
    content: "";
    height: 50px;
    width: 50px;
    position: absolute;
    top: 0;
    left: 0;
    background-image: url('https://github.com/amidaware/reporting-templates/blob/471b2d34fae8029d8c05b6c3d000c027648a07e4/assets/2orig1%20(256).png?raw=true');
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
  }
  @top-center {
    background: #ffcc00;
    content: '';
    display: block;
    height: .05cm;
    opacity: .5;
    width: 100%;
  }
  
  @bottom-left {
    background: #ffcc00;
    content: counter(page);
    height: 1cm;
    text-align: center;
    width: 1cm;
  }
  @bottom-center {
    background: #ffcc00;
    content: '';
    display: block;
    height: .05cm;
    opacity: .5;
    width: 100%;
  }
  @bottom-right {
    content: string(heading);
    font-size: 9pt;
    height: 1cm;
    vertical-align: middle;
    width: 100%;
  }
}
@page :blank {
  @top-left { background: none; content: '' }
  @top-center { content: none }
  @top-right { content: none }
}
@page no-chapter {
  @top-left { background: none; content: none }
  @top-center { content: none }
  @top-right { content: none }
}
@page :first {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/report-cover.jpg) no-repeat center;
  background-size: cover;
  margin: 0;
}
@page chapter {
  background: #ffcc00;
  margin: 0;
  @top-left { content: none }
  @top-center { content: none }
  @top-right { content: none }
}
.page-break {
  page-break-before: always; /* For older browsers */
  break-before: page; /* For newer browsers */
}


html {
  color: #393939;
  font-family: Fira Sans;
  font-size: 11pt;
  font-weight: 300;
  line-height: 1.5;
}

h1 {
  color: #ffcc00;
  font-size: 38pt;
  margin: 5cm 2cm 0 2cm;
  page: no-chapter;
  width: 100%;
}
h2, h3, h4 {
  color: black;
  font-weight: 400;
}
h2 {
  break-before: always;
  font-size: 28pt;
  string-set: heading content();
}
h3 {
  font-weight: 300;
  font-size: 15pt;
}
h4 {
  font-size: 13pt;
}

#cover {
  align-content: space-between;
  display: flex;
  flex-wrap: wrap;
}
#cover address {
  position: absolute; /* Absolute positioning */
  bottom: 0; /* Stick to the bottom */
  width: 100%; /* Full width */
  text-align: center; /* Center the text */
  background: transparent; /* Optional: make background transparent or adjust as needed */
  margin: 0; /* Remove margins */
  padding: 20px 0; /* Add padding at the top and bottom */
}
#cover address:first-of-type {
  padding-left: 3cm;
}
#contents {
  page: no-chapter;
}
#contents h2 {
  font-size: 20pt;
  font-weight: 400;
  margin-bottom: 3cm;
}
#contents h3 {
  font-weight: 500;
  margin: 3em 0 1em;
}
#contents h3::before {
  background: #ffcc00;
  content: '';
  display: block;
  height: .08cm;
  margin-bottom: .25cm;
  width: 2cm;
}
#contents ul {
  list-style: none;
  padding-left: 0;
}
#contents ul li {
  border-top: .25pt solid #c1c1c1;
  margin: .25cm 0;
  padding-top: .25cm;
}
#contents ul li::before {
  color: #ffcc00;
  content: 'â€¢ ';
  font-size: 40pt;
  line-height: 16pt;
  vertical-align: bottom;
}
#contents ul li a {
  color: inherit;
  text-decoration-line: inherit;
}
#contents ul li a::before {
  content: target-text(attr(href));
}
#contents ul li a::after {
  color: #ffcc00;
  content: target-counter(attr(href), page);
  float: right;
}

#columns section {
  columns: 2;
  column-gap: 1cm;
  padding-top: 1cm;
}
#columns section p {
  text-align: justify;
}
#columns section p:first-of-type {
  font-weight: 700;
}

#skills h3 {
  background: #ffcc00;
  margin: 0 -3cm 1cm;
  padding: 1cm 1cm 1cm 3cm;
  width: 21cm;
}
#skills section {
  padding: .5cm 0;
}
table.GeneratedTable {
  width: 100%;
  background-color: #ffffff;
  border-collapse: collapse;
  border-width: 2px;
  border-color: #ffcc00;
  border-style: solid;
  color: #000000;
}

table.GeneratedTable td, table.GeneratedTable th {
  border-width: 2px;
  border-color: #ffcc00;
  border-style: solid;
  padding: 3px;
}

table.GeneratedTable thead {
  background-color: #ffcc00;
}
#skills section#tablecontent::before {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/table-content.svg) no-repeat center #ffcc00;
  background-size: 50%;
  content: '';
  display: inline-block;
  float: left;
  height: 2cm;
  margin-right: .5cm;
  vertical-align: middle;
  width: 2cm;
}
#skills section#heading::before {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/heading.svg) no-repeat center #ffcc00;
  background-size: 50%;
  content: '';
  display: inline-block;
  float: left;
  height: 2cm;
  margin-right: .5cm;
  vertical-align: middle;
  width: 2cm;
}
#skills section#multicolumns::before {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/multi-columns.svg) no-repeat center #ffcc00;
  background-size: 50%;
  content: '';
  display: inline-block;
  float: left;
  height: 2cm;
  margin-right: .5cm;
  vertical-align: middle;
  width: 2cm;
}
#skills section#internallinks::before {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/internal-links.svg) no-repeat center #ffcc00;
  background-size: 50%;
  content: '';
  display: inline-block;
  float: left;
  height: 2cm;
  margin-right: .5cm;
  vertical-align: middle;
  width: 2cm;
}
#skills section#style::before {
  background: url(https://raw.githubusercontent.com/CourtBouillon/weasyprint-samples/master/report/style.svg) no-repeat center #ffcc00;
  background-size: 50%;
  content: '';
  display: inline-block;
  float: left;
  height: 2cm;
  margin-right: .5cm;
  vertical-align: middle;
  width: 2cm;
}
#skills section h4 {
  margin: 0;
}
#skills section p {
  margin-top: 0;
}

#offers {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
}
#offers h2, #offers h3 {
  width: 100%;
}
#offers section {
  width: 30%;
}
#offers section h4 {
  margin-bottom: 0;
}
#offers section ul {
  list-style: none;
  margin: 0;
  padding-left: 0;
}
#offers section ul li:not(:last-of-type) {
  margin: .5cm 0;
}
#offers section p {
  background: #ffcc00;
  display: block;
  font-size: 15pt;
  font-weight: 700;
  margin-bottom: 0;
  padding: .25cm 0;
  text-align: center;
}

#chapter {
  align-items: center;
  display: flex;
  height: 297mm;
  justify-content: center;
  page: chapter;
}

#typography section {
  display: flex;
  flex-wrap: wrap;
  margin: 1cm 0;
}
#typography section h4 {
  border-top: 1pt solid;
  flex: 1 25%;
  margin: 0;
}
#typography section h4 + * {
  flex: 1 75%;
  margin: 0;
  padding-left: .5cm;
}
#typography section p {
  text-align: justify;
}
#typography section ul {
  line-height: 2;
  list-style: none;
}
#typography section#smallcaps p {
  font-variant: small-caps;
}
#typography section#ligatures dl {
  display: flex;
  flex-wrap: wrap;
}
#typography section#ligatures dl dt {
  font-weight: 400;
  width: 30%;
}
#typography section#ligatures dl dd {
  flex: 1 70%;
  margin: 0;
  padding: 0;
}
#typography section#ligatures .none {
  font-variant-ligatures: none;
}
#typography section#ligatures .common {
  font-variant-ligatures: common-ligatures;
}
#typography section#ligatures .discretionary {
  font-variant-ligatures: discretionary-ligatures;
}
#typography section#ligatures .contextual {
  font-variant-ligatures: contextual;
}
#typography section#numbers dl {
  display: flex;
  flex-wrap: wrap;
}
#typography section#numbers dl dt {
  font-weight: 400;
  width: 30%;
}
#typography section#numbers dl dd {
  flex: 1 70%;
  margin: 0;
  padding: 0;
}
#typography section#numbers #fractions {
  font-variant-numeric: diagonal-fractions;
}
#typography section#numbers #ordinals {
  font-variant-numeric: ordinal;
}
#typography section#numbers #slashed {
  font-variant-numeric: slashed-zero;
}
#typography section#numbers #super {
  font-variant-position: super;
}
#typography section#numbers #sub {
  font-variant-position: sub;
}
#typography section#figures dl {
  columns: 4;
}
#typography section#figures dl dt {
  font-weight: 400;
}
#typography section#figures dl dd {
  display: flex;
  margin: 0;
  padding: 0;
}
#typography section#figures dl dd ul {
  padding: 0 1em 0 0;
}
#typography section#figures #oldstyle {
  font-variant-numeric: oldstyle-nums;
}

template.template_md:
{% block content %}
<style>
  .disk-bar-container {
    width: 100px;
    height: 15px;
    background-color: #e0e0e0;
    border: 1px solid #ccc;
    border-radius: 3px;
    margin-top: 2px;
    margin-bottom: 5px;
    overflow: hidden;
  }
  .disk-bar {
    height: 100%;
    background-color: #4CAF50; /* Green */
  }
  .disk-bar.warning {
    background-color: #ff9800; /* Orange */
  }
  .disk-bar.critical {
    background-color: #f44336; /* Red */
  }
</style>

~~A cover~~
# Agent List for {{client.name}}
<address>
Company
123 anywhere st
City, State zip
</address>
<address>
me@here.com
https://example.com
</address>
~~/A~~
~~A~~
{{ client.name }} Agents List
<div class="page-break"></div>
<table class="GeneratedTable">
  <thead>
    <tr>
      <td colspan="4" rowspan="1" style="vertical-align: top;">Agent Info</td>
      <td style="vertical-align: top;">Site Name</td>
    </tr>
  </thead>
  <tbody>
    {% for item in data_sources.agentAllFields %}
    <tr>
      <td style="vertical-align: top; text-align: right; width: 100px;">
        <span style="font-weight: bold;">Name:</span><br>
        <span style="font-weight: bold;">User:</span><br>
        <span style="font-weight: bold;">OS:</span><br>
        <span style="font-weight: bold;">Last Seen:</span><br>
      </td>
      <td style="vertical-align: top; width: 250px;">
        {{ item.hostname }}<br>
        {{ item.last_logged_in_user }}<br>
        {{ item.operating_system.split(',')[0] if item.operating_system else 'N/A' }}<br>
        
        <!-- THIS BLOCK IS NOW FIXED AND SAFE -->
        {% if item.last_seen and report_run_timestamp and (report_run_timestamp - item.last_seen).days > 7 %}
          <span style="color: red;">{{ item.last_seen.strftime('%Y-%m-%d %H:%M') }}</span><br>
        {% elif item.last_seen %}
          {{ item.last_seen.strftime('%Y-%m-%d %H:%M') }}<br>
        {% else %}
          N/A<br>
        {% endif %}

      </td>
      <td style="vertical-align: top; text-align: right; width: 100px;">
        <span style="font-weight: bold;">CPU:</span><br>
        <span style="font-weight: bold;">RAM:</span><br>
        <span style="font-weight: bold;">Disks:</span><br>
      </td>
      <td style="vertical-align: top;">
        {{ item.wmi_detail.cpu[0][0].Name.replace('(R)', '').replace('(TM)', '').split('@')[0].strip() if item.wmi_detail and item.wmi_detail.cpu and item.wmi_detail.cpu[0][0].Name else 'N/A' }}<br>
        {{ item.total_ram }}GB <br>
        {% for disk in item.disks %}
          {% if not loop.previtem or loop.previtem.device != disk.device %}
            {% set total_gb = disk.total | replace('GB','') | float(0) %}
            {% set free_gb = disk.free | replace('GB','') | float(0) %}
            {% set used_gb = total_gb - free_gb %}
            {% set usage_percent = (used_gb / total_gb) * 100 if total_gb > 0 else 0 %}
            
            {% set bar_class = '' %}
            {% if usage_percent > 90 %}
              {% set bar_class = 'critical' %}
            {% elif usage_percent > 80 %}
              {% set bar_class = 'warning' %}
            {% endif %}

            {{ disk.device }} {{ disk.free }} free of {{ disk.total }}<br>
            <div class="disk-bar-container">
              <div class="disk-bar {{ bar_class }}" style="width: {{ usage_percent }}%;"></div>
            </div>
          {% endif %}
        {% else %}
          N/A<br>
        {% endfor %}
      </td>
      <td style="vertical-align: top;">{{ item.site__name }}</td>
    </tr>
    {% endfor %}
  </tbody>
</table>
~~/A~~
{% endblock %}
