template.template_md:
{% block content %}
<style>
  @page {
    margin: 0.5in;
    size: letter landscape;
    /* or A4 landscape */
  }

  /* Style for the filter dropdowns inside the table headers */
  .report-table thead th select {
    width: 100%;
    margin-top: 5px;
    padding: 4px;
    border-radius: 4px;
    border: 1px solid #ccc;
    font-weight: normal; /* Keep dropdown text normal weight */
  }
</style>

<div class="report-header">
  <div class="header-logo">
    <img src="https://github.com/amidaware/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true" alt="Amidaware Logo" style="width: 80px; height: auto;">
  </div>
  <div class="header-titles">
    <h1>TRMM Agents Installation Report</h1>
    <h2 id="report-subtitle">Client: All | Site: All</h2>
  </div>
  <div class="header-date">
    <h2>Report Date:<br>{{ report_run_timestamp.strftime('%B %d, %Y') }}</h2>
  </div>
</div>

{% set clients = [] %}
{% set sites = [] %}
{% for item in data_sources.agents %}
  {% if item.site__client__name not in clients %}
    {% set _ = clients.append(item.site__client__name) %}
  {% endif %}
  {% if item.site__name not in sites %}
    {% set _ = sites.append(item.site__name) %}
  {% endif %}
{% endfor %}

<div class="section">
  <table class="report-table">
    <thead>
      <tr>
        <th>
          Client
          <select id="client-filter">
            <option value="all">All Clients</option>
            {% for client in clients | sort %}
            <option value="{{ client }}">{{ client }}</option>
            {% endfor %}
          </select>
        </th>
        <th>
          Site
          <select id="site-filter">
            <option value="all">All Sites</option>
            {% for site in sites | sort %}
            <option value="{{ site }}">{{ site }}</option>
            {% endfor %}
          </select>
        </th>
        <th>Agent</th>
        <th>User</th>
        <th>Installation Date</th>
      </tr>
    </thead>
    <tbody id="report-table-body">
      {% for item in data_sources.agents %}
      <tr>
        <td>{{ item.site__client__name }}</td>
        <td>{{ item.site__name }}</td>
        <td>{{ item.hostname }}</td>
        <td>{{ item.last_logged_in_user }}</td>
        <td>{% if item.created_time %}{{ item.created_time.strftime('%Y-%m-%d %H:%M') }}{% else %}N/A{% endif %}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<script>
  // This script runs after the report is loaded in the browser
  document.addEventListener("DOMContentLoaded", function () {
    const clientFilter = document.getElementById("client-filter");
    const siteFilter = document.getElementById("site-filter");
    const tableBody = document.getElementById("report-table-body");
    const reportSubtitle = document.getElementById("report-subtitle");
    const tableRows = tableBody.getElementsByTagName("tr");

    function applyFilters() {
      const selectedClient = clientFilter.value;
      const selectedSite = siteFilter.value;

      // Update the report subtitle to reflect the current filters
      let subtitle = `Client: ${selectedClient === 'all' ? 'All' : selectedClient}`;
      subtitle += ` | Site: ${selectedSite === 'all' ? 'All' : selectedSite}`;
      reportSubtitle.textContent = subtitle;

      // Loop through all rows in the table and hide/show them based on selections
      for (let i = 0; i < tableRows.length; i++) {
        const row = tableRows[i];
        const clientCell = row.cells[0].textContent.trim();
        const siteCell = row.cells[1].textContent.trim();

        const clientMatch = (selectedClient === "all") || (clientCell === selectedClient);
        const siteMatch = (selectedSite === "all") || (siteCell === selectedSite);

        if (clientMatch && siteMatch) {
          row.style.display = ""; // Show the row
        } else {
          row.style.display = "none"; // Hide the row
        }
      }
    }

    // Add event listeners to run the filter function whenever a dropdown is changed
    clientFilter.addEventListener("change", applyFilters);
    siteFilter.addEventListener("change", applyFilters);
  });
</script>
{% endblock %}
