template.template_md:
{% block content %}
<style>
  @media print {
    .timeline-bar {
      page-break-inside: avoid;
    }
  }

  .small-logo {
    height: 80px;
    width: auto;
  }

  .timeline-container {
    margin-top: 40px;
    padding-right: 20px;
  }

  .timeline-row {
    margin-bottom: 10px;
  }

  .timeline-bar {
    position: relative;
    height: 16px;
    background-color: green;
    width: 100%;
  }

  .timeline-block {
    box-sizing: border-box;
    position: absolute;
    height: 100%;
    background-color: red;
    border: 1px solid #000;
  }

  .timeline-label {
    font-size: 11px;
    margin-bottom: 4px;
  }
</style>

<div class="report-header">
  <img src="https://github.com/amidaware/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true"
       alt="Logo"
       class="header-logo small-logo">
  <div class="header-date">
    <h1>{{ client.name }} - Agent Uptime Report</h1>
    <h2>Report Date: {{ report_run_timestamp.strftime('%Y-%m-%d') }}</h2>
  </div>
</div>

{% set cutoff = timedelta_30_days %}
{% set alert_events = [] %}

<div class="section timeline-container" id="timelineChart">
  <h2>Agent Timeline (Green = Online, Red = Offline)</h2>
</div>

<h2>Downtime List</h2>

<table class="report-table">
  <thead>
    <tr>
      <th>Agent ID</th>
      <th>Message</th>
      <th>Time Off</th>
      <th>Time On</th>
      <th>Downtime</th>
    </tr>
  </thead>
  <tbody>
    {% for item in data_sources.allFieldsAlerts %}
      {% if item.alert_type == "availability"
            and item.resolved
            and item.alert_time is defined
            and item.resolved_on is defined
            and item.agent__hostname is defined
            and item.alert_time >= cutoff %}
        {% set downtime = (item.resolved_on - item.alert_time).total_seconds() / 60 %}
        <tr>
          <td>{{ item.agent__hostname }}</td>
          <td>{{ item.message }}</td>
          <td>{{ item.alert_time.strftime('%Y-%m-%d %H:%M') }}</td>
          <td>{{ item.resolved_on.strftime('%Y-%m-%d %H:%M') }}</td>
          {% set dt_mins = downtime | round(0, 'floor') %}
          {% set dt_days = dt_mins // 1440 %}
          {% set dt_hrs  = (dt_mins % 1440) // 60 %}
          {% set dt_min  = dt_mins % 60 %}
          <td>
            {% if dt_days > 0 %}{{ dt_days }}d {% endif %}
            {% if dt_hrs  > 0 %}{{ dt_hrs  }}h {% endif %}
            {% if dt_min > 0 or (dt_days == 0 and dt_hrs == 0) %}{{ dt_min }}m{% endif %}
          </td>
        </tr>
        {% set _ = alert_events.append({
          "agent": item.agent__hostname,
          "start": item.alert_time.isoformat(),
          "end":   item.resolved_on.isoformat()
        }) %}
      {% endif %}
    {% endfor %}
  </tbody>
</table>

<script>
  document.addEventListener("DOMContentLoaded", function () {
    const events = {{ alert_events | tojson | safe }};
    const container = document.getElementById("timelineChart");
    container.style.margin = "0 20px";

    if (!events.length) {
      const p = document.createElement("p");
      p.textContent = "No downtime data found.";
      container.appendChild(p);
      return;
    }

    const minTime = Math.min(...events.map(e => new Date(e.start).getTime()));
    const maxTime = Math.max(...events.map(e => new Date(e.end).getTime()));
    const range   = maxTime - minTime;

    const grouped = {};
    events.forEach(e => {
      if (!grouped[e.agent]) grouped[e.agent] = { events: [], totalDowntime: 0 };
      const start   = new Date(e.start);
      const end     = new Date(e.end);
      const minutes = (end - start) / 60000;
      grouped[e.agent].events.push({ start, end });
      grouped[e.agent].totalDowntime += minutes;
    });

    Object.entries(grouped).forEach(([agent, data]) => {
      const totalMins = Math.round(data.totalDowntime);
      const days      = Math.floor(totalMins / 1440);
      const hrs       = Math.floor((totalMins % 1440) / 60);
      const min       = totalMins % 60;
      const summary   = `(Offline ${days} day${days !== 1 ? 's' : ''} ` +
                        `${hrs} hr${hrs !== 1 ? 's' : ''} ${min} min in last 30 days)`;

      const row  = document.createElement("div");
      row.className = "timeline-row";
      row.style.marginBottom = "30px";

      const label = document.createElement("div");
      label.className = "timeline-label";
      label.innerHTML = `<strong>${agent}</strong> ${summary}`;
      row.appendChild(label);

      const barWrapper = document.createElement("div");
      barWrapper.style.position = "relative";

      const bar = document.createElement("div");
      bar.className = "timeline-bar";
      bar.style.marginBottom = "4px";

      data.events.forEach(({ start, end }) => {
        const offset = ((start.getTime() - minTime) / range) * 100;
        const width  = ((end.getTime() - start.getTime()) / range) * 100;

        const block = document.createElement("div");
        block.className = "timeline-block";
        block.style.left  = offset + "%";
        block.style.width = width  + "%";
        block.title = `${start.toLocaleString()} – ${end.toLocaleString()}`;
        bar.appendChild(block);
      });

      const tickRow = document.createElement("div");
      tickRow.style.borderTop = "1px solid #ccc";
      tickRow.style.position  = "relative";
      tickRow.style.height    = "16px";

      const totalDays = Math.ceil(range / (1000 * 60 * 60 * 24));
      for (let i = 0; i <= totalDays; i++) {
        const tick = document.createElement("div");
        tick.style.position = "absolute";
        tick.style.left     = ((i / totalDays) * 100) + "%";
        tick.style.height   = "100%";
        tick.style.borderLeft = "1px dashed #888";
        tick.style.fontSize   = "9px";
        tick.style.color      = "#333";
        tick.style.paddingLeft = "2px";
        tick.style.whiteSpace  = "nowrap";

        const dayDate = new Date(minTime + i * 24 * 60 * 60 * 1000);
        tick.textContent = dayDate.toLocaleDateString(undefined, {
          weekday: 'short', day: 'numeric'
        });

        tickRow.appendChild(tick);
      }

      barWrapper.appendChild(bar);
      barWrapper.appendChild(tickRow);
      row.appendChild(barWrapper);
      container.appendChild(row);
    });

    setTimeout(() => {
      window.html2canvas = window.html2canvas || window["html2canvas"];
      if (typeof html2canvas === "function") {
        html2canvas(container).then(canvas => {
          const img = document.createElement("img");
          img.src = canvas.toDataURL("image/png");
          img.style.maxWidth = "100%";
          img.alt = "Agent Timeline Snapshot";
          container.innerHTML = "";
          container.appendChild(img);
        }).catch(err => console.error("html2canvas failed:", err));
      } else {
        console.warn("html2canvas not loaded.");
      }
    }, 300);
  });
</script>
{% endblock %}
